<?phpinclude_once('startup.php');include_once('model.php');// Установка параметров, подключение к БД, запуск сессии.startup();$title = 'Изменение статьи';// Обработка отправки формы.if (isset($_POST['edit'])){		if (articles_edit($_POST['id'], $_POST['title'], $_POST['content']))	{		header('Location: editor.php');		die();	}	$art['id'] = $_POST['id'];	$art['title'] = $_POST['title'];	$art['content'] = $_POST['content'];}elseif (isset($_POST['delete'])) {	if (articles_delete($_POST['id']))	{		header('Location: editor.php');		die();	}}else{	$art=articles_get($_GET['id']);	$art['id'] = $_GET['id'];}// Внутренний шаблон.$content = view_include('theme/v_edit.php', $art);include ('view.php');