<?phpinclude_once('model/model.php');$title = 'Главная';// Извлечение статей.$art = new Article();$articles = $art->articles_all();// создание introforeach ($articles as $key => $article)	$articles[$key]['intro'] = $art->articles_intro($article);// Внутренний шаблон.$content = $art->view_include('view/v_index.php', array('articles' => $articles));include ('controller/view.php');